(function(){tinymce.PluginManager.requireLangPack('flash');tinymce.create('tinymce.plugins.flashPlugin',{init:function(ed,url){ed.addCommand('mceflash',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('flash.delta_width',0)),height:140+parseInt(ed.getLang('flash.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('flash',{title:'flash.desc',cmd:'mceflash',image:url+'/img/flash.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('flash',n.nodeName=='flash');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'flash plugin',author:'SP',authorurl:'http://www.malleo-cms.com',infourl:'http://www.malleo-cms.com',version:"1.0"};}});tinymce.PluginManager.add('flash',tinymce.plugins.flashPlugin);})();